// Home.jsx

import React from "react";
import { motion } from "framer-motion";
import { Button } from "@/components/ui/button";
import { Typewriter } from "react-simple-typewriter";
import profilePic from "../assets/vartika.jpg"; // Make sure to replace this with your actual image path

const Home = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 to-white dark:from-gray-900 dark:to-black flex items-center justify-between px-10 py-20">
      {/* Left Section */}
      <div className="w-1/2 space-y-6">
        <motion.h1 
          className="text-5xl font-bold text-gray-900 dark:text-white"
          initial={{ opacity: 0, y: -20 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 0.6 }}
        >
          Hi, I’m Vartika.
        </motion.h1>

        <motion.h2 
          className="text-2xl font-semibold text-purple-700 dark:text-purple-300"
          initial={{ opacity: 0 }} 
          animate={{ opacity: 1 }} 
          transition={{ delay: 0.4 }}
        >
          <Typewriter
            words={["Full Stack Developer", "Problem Solver", "Tech Enthusiast"]}
            loop={0}
            cursor
            cursorStyle='|'
            typeSpeed={80}
            deleteSpeed={60}
            delaySpeed={1500}
          />
        </motion.h2>

        <ul className="text-gray-700 dark:text-gray-300 text-lg space-y-2">
          <li>✅ Build clean, scalable apps</li>
          <li>✅ Solve real-world problems</li>
          <li>✅ Learn fast, lead better</li>
        </ul>

        <div className="flex gap-4 pt-4">
          <Button className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-2 rounded-xl shadow">
            View Portfolio
          </Button>
          <Button variant="outline" className="text-purple-700 border-purple-600 px-6 py-2 rounded-xl">
            Download Resume
          </Button>
        </div>
      </div>

      {/* Right Section */}
      <motion.div 
        className="w-1/2 flex justify-center items-center"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.8 }}
      >
        <img 
          src={profilePic} 
          alt="Vartika Shah" 
          className="rounded-full w-[400px] h-[400px] object-cover shadow-2xl border-4 border-white dark:border-gray-800 hover:scale-105 transition transform duration-500"
        />
      </motion.div>
    </div>
  );
};

export default Home;
